class WatchDog
{
	private function query($obj)
	{
		$query='';
		
		switch($obj->method)
		{
			case'getVeloStations':
				$query='index.txt';
			break;
			case'getMetro':
				$query='index.txt';
			break;
			default:
				return false;
			break;
		}
		
		$content=json_decode(file_get_contents('db/'.$obj->town.'/'.$obj->method.'/'.$query));
		
		return $content;
	}
    public function helo($nothing){
        return true;
    }
	public function connected($arr){
		return true;
	}
	/**
