	public function getVeloStations($data,$dunno)
	{
		$return=array();
		$obj=new stdClass();
		$obj->method='getVeloStations';
		$obj->town=$data['town'];
		
		$return=$this->query($obj);
		
		return $return;
	}
	public function getMetro($from,$dunno)
	{
		$obj=new stdClass();
		$obj->method='getMetro';
		$obj->town=$from;
		return $this->query($obj);
	}
